import { useEffect, useState } from "react";
import Axios from "../axios.js";
import WorkoutDetails from "../components/workoutDetails.js";
import WorkoutForm from "../components/workoutForm.js";

const Home = () => {
  // To ensure that the data remains
  const [workouts, setWorkout] = useState([]);

  // We use useEffect because useEffect will be run at the very start and any time the dependency is changed. Therefore, all the data
  //   will be fetched at the very start.
  useEffect(() => {
    const fetchAllWorkout = async () => {
      try {
        const response = await Axios.get("/home");
        console.log(response.data);
        setWorkout(response.data);
      } catch (err) {
        console.log(err);
      }
    };
    fetchAllWorkout();
  }, []);

  return (
    <div className="home">
      {workouts.map(
        (row) =>
          row.status === "0" && (
            <div className="tasks">
              <h2>Current Tasks</h2>
              <div className="workouts">
                <WorkoutDetails workout={row} key={row._id} />
              </div>
            </div>
          )
      )}
      {workouts.map(
        (row) =>
          row.status === "1" && (
            <div className="tasks">
              <h2>Completed Tasks</h2>
              <div className="workouts">
                <WorkoutDetails workout={row} key={row._id} />
              </div>
            </div>
          )
      )}
      {/* <button onClick={createTask}>Create</button> */}
      {/* <WorkoutForm /> */}
    </div>

    // <div className="home">
    //   <div className="tasks">
    //     <h2>Current Tasks</h2>
    //   </div>
    //   <div className="workouts">
    //     {workouts.map(
    //       (row) =>
    //         row.status === "1" && <WorkoutDetails workout={row} key={row._id} />
    //     )}
    //   </div>
    //   <div className="tasks">
    //     <h2>Completed Tasks</h2>
    //   </div>
    //   {/* <button onClick={createTask}>Create</button> */}
    //   {/* <WorkoutForm /> */}
    // </div>
  );
};

export default Home;
