import Axios from "../axios.js";
import logo from "../images/muscle (3).png";
import deleteBtn from "../images/delete.png";
import { useState } from "react";

const WorkoutDetails = ({ workout }) => {
  const [check, setCheck] = useState("");

  const handleCheck = async (e, id) => {
    try {
      if (e.target.checked) {
        let status = "1";
        const response = await Axios.post("/status/" + id);
      } else {
        console.log("gfgf");
      }
    } catch {
      console.log("Error");
    }
  };

  const handleDelete = async (id) => {
    try {
      await Axios.delete("/del/" + id);
      window.location.reload();
    } catch (error) {
      console.log(error.response);
    }
  };

  return (
    <div className="workout-details">
      <span>Author:</span>
      <div className="logo">
        <img src={logo} alt="" />
      </div>
      <div className="detail-title">
        <h3>{workout.workout}</h3>
      </div>
      <div className="detail-load">
        <p>
          <strong style={{ color: "#f37a24" }}>Load (kg): </strong>
          {workout.loads}
        </p>
      </div>
      <div className="detail-reps">
        <p>
          <strong style={{ color: "#f37a24" }}>Number of reps: </strong>
          {workout.reps}
        </p>
      </div>
      <div className="del">
        <img src={deleteBtn} alt="" onClick={handleDelete} />
      </div>
      <div className="check-box">
        <input
          type="checkbox"
          id="completed"
          name="completed"
          onChange={handleCheck}
        />
      </div>
    </div>
  );
};

export default WorkoutDetails;
