import React, { useState } from "react";
import photo1 from "../images/ali3.jpg";
import { Link } from "react-router-dom";
import Axios from "../axios.js";

const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, errorMsg] = useState("");

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await Axios.post("/login", {
        username: username,
        password: password,
      });
      console.log(response.data);
      if (response.data.auth !== true) {
        errorMsg(response.data.msg);
      } else {
        errorMsg("");
        window.location.href = "/home";
      }
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <div className="login">
      <div className="front">
        <div className="main-text animate__animated animate__fadeInLeft">
          <span>
            <h1>
              "Float like a butterfly. Sting like a bee. You can't hit what your
              eyes don't see."
            </h1>
          </span>
        </div>
        <div class="photo animate__animated animate__fadeRight">
          <img src={photo1} alt="dd" />
        </div>
      </div>
