import { useState } from "react";
import { Link } from "react-router-dom";

const Navbar = (handleAddTask) => {
  const [toggle, setToggle] = useState(false);
  const handleToggle = (e, handleAddTask) => {
    if (e.target.click) {
      setToggle((prev) => !prev);
      handleAddTask(toggle);
    }
  };

  return (
    <div className="navbar">
      <div className="nav-title">
        <h1>Workout Buddy</h1>
      </div>
      <div className="nav-content">
        <div className="nav-task">
          <span onClick={(event) => handleClick(100)}>Add task</span>
        </div>
        <Link to="/register">
          <div className="nav-signnup">
            <span>Sign up</span>
          </div>
        </Link>
        <Link to="/">
          <div className="nav-logout">
            <span>Logout</span>
          </div>
        </Link>
      </div>
    </div>
  );
};

export default Navbar;
