import Axios from "../axios.js";
import { useState, useEffect } from "react";

const EditForm = ({ editID }) => {
  const [title, setTitle] = useState("");
  const [load, setLoad] = useState("");
  const [reps, setReps] = useState("");
  const [editWorkout, setEditWorkout] = useState([]);

  // const handleSubmit = async (e) => {
  //   e.preventDefault();
  //   if (title !== "") {
  //     try {
  //       const response = await Axios.post("/add", {
  //         title: title,
  //         load: load,
  //         reps: reps,
  //       });
  //       window.location.reload();
  //     } catch (error) {
  //       console.log(error);
  //     }
  //   }
  // };

  useEffect(() => {
    const fetchWorkoutToEdit = async (editID) => {
      try {
        const response = await Axios.get("/edit/" + editID);
        setEditWorkout(response.data[0]);
      } catch (err) {
        console.log(err);
      }
    };
    fetchWorkoutToEdit(editID);
  }, []);
  console.log(editWorkout.workout);

  return (
    <div className="workout-form animate__animated animate__fadeInDown">
      <div className="card">
        <div className="form-container">
          <form className="formClass">
            <h2>Edit Workout</h2>

            <label className="w-form-label" style={{ "margin-right": "580px" }}>
              Exercise Title:
            </label>
            <input
              type="text"
              value={editWorkout.workout}
              onChange={(e) => setTitle(e.target.value)}
              id="title"
            />

            <label className="w-form-label" style={{ "margin-right": "580px" }}>
              Load (in kg):
            </label>
            <input
              type="text"
              value={editWorkout.loads}
              onChange={(e) => setLoad(e.target.value)}
              id="load"
            />

            <label className="w-form-label" style={{ "margin-right": "550px" }}>
              Number of Reps:
            </label>
            <input
              type="text"
              value={editWorkout.reps}
              onChange={(e) => setReps(e.target.value)}
              id="reps"
            />
            {/* <button onClick={handleSubmit}>Add Workout</button> */}
          </form>
        </div>
      </div>
    </div>
  );
};

export default EditForm;
