import React, { useEffect, useState } from "react";
import photo1 from "../images/ali3-1.jpg";
import { Link } from "react-router-dom";
import Axios from "../axios.js";

useEffect(() => {
  /*
  Query logic
  */
  console.log("i fire once");
}, []);

const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, errorMsg] = useState("");

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await Axios.post("/login", {
        username: username,
        password: password,
      });
      if (response.data.auth !== true) {
        errorMsg(response.data.msg);
      } else {
        errorMsg("");
        window.location.href = "/home";
      }
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <div className="login">
      {/* <div class="photo animate__animated animate__fadeInRight animate__slow">
        <img src={photo1} alt="dd" />
      </div>
      <div className="main-text animate__animated animate__fadeInLeft animate__slower">
        <span>
          <h1>
            "Float like a butterfly. Sting like a bee. You can't hit what your
            eyes don't see."
          </h1>
        </span>
      </div> */}

      <div className="card">
        <form action="">
          <span>
            <h2>Login</h2>
          </span>
          <label>Enter Username :</label>
          <input
            required
            type="text"
            value={username}
            placeholder="username"
            onChange={(e) => {
              setUsername(e.target.value);
            }}
          ></input>
          <label>Enter Pasword :</label>
          <input
            required
            type="password"
            value={password}
            placeholder="password"
            onChange={(e) => {
              setPassword(e.target.value);
            }}
          ></input>
          <button type="submit" onClick={handleLogin}>
            Login
          </button>
          {error && <p className="errorMsg">{error}</p>}
          <div className="form-footer">
            <hr></hr>
            <p>
              Don't have an account?<Link to="/register">Register</Link>
            </p>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Login;
