import React from "react";
import { Link } from "react-router-dom";

const LoginNav = ({ setAddTask }) => {
  const handleAddTask = (e) => {
    if (e.target.click) {
      setAddTask((prev) => !prev);
    }
  };

  return (
    <div className="navbar">
      <div className="navbar-content justify-between items-center">
        <div className="nav-title">
          <h1 className="text-4xl font-bold ">Workout Buddy</h1>
        </div>
        <div className="nav-content">
          <div className="nav-task">
            <span onClick={(e) => handleAddTask(e)}>Add task</span>
          </div>
          <Link to="/">
            <div className="nav-logout">
              <span>Logout</span>
            </div>
          </Link>
        </div>
      </div>
    </div>
  );
};

export default LoginNav;
