import axios from "axios";
import { useState } from "react";
import { Link, useNavigate } from "react-router-dom";

const WorkoutForm = () => {
  const [tem, setTemp] = useState({
    title: "",
    load: "",
    reps: "",
  });

  const [error, setError] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) => {
    setTemp((prev) => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  };

  const handleSubmit = async (e) => {
    alert("ff");
    e.preventDefault();
    try {
      await axios.post("http://localhost:3001/api", tem);
      navigate("/");
    } catch (err) {
      console.log(err);
      setError(true);
    }
  };

  return (
    <div class="card">
      <div class="container">
        <form className="formClass">
          <h3>Add a New Workout</h3>

          <label>Excersise Title:</label>
          <input type="text" onChange={handleChange} name="title" />

          <label>Load (in kg):</label>
          <input type="number" onChange={handleChange} name="load" />

          <label>Number of Reps:</label>
          <input type="number" onChange={handleChange} name="reps" />
          <button onClick={handleSubmit}>Add Workout</button>
        </form>
      </div>
    </div>
  );
};

export default WorkoutForm;
