import Axios from "../axios.js";
import logo from "../images/muscle (3).png";

const WorkoutDetails = ({ workout }) => {
  const handleDelete = async (id) => {
    try {
      await Axios.delete("/del/" + id);
      window.location.reload();
    } catch (error) {
      console.log(error.response);
    }
  };

  return (
    // <div className="workout-details">
    //   <div className="logo">
    //     <img src={logo} alt="" />
    //   </div>
    //   <h1>{workout.workout}</h1>
    // <p>
    //   <strong>Load (kg): </strong>
    //   {workout.loads}
    // </p>
    // <p>
    // <strong>Number of reps: </strong>
    // {workout.reps}
    // </p>
    //   <button onClick={() => handleDelete(workout.id)}>Delete</button>
    // </div>

    <div className="workout-details">
      <span>Author:</span>
      <div className="logo">
        <img src={logo} alt="" />
      </div>
      <div className="detail-title">
        <h3>{workout.workout}</h3>
      </div>
      <div className="detail-load">
        <p>
          <strong style={{ color: "#f37a24" }}>Load (kg): </strong>
          {workout.loads}
        </p>
      </div>
      <div className="detail-reps">
        <p>
          <strong style={{ color: "#f37a24" }}>Number of reps: </strong>
          {workout.reps}
        </p>
      </div>
    </div>
  );
};

export default WorkoutDetails;
