import { useEffect, useState } from "react";
import Axios from "../axios.js";

const Form = () => {
    const [workouts, setWorkout] = useState([]);

    useEffect(() => {
        const fetchAllWorkout = async () => {
            try {
                const res = await Axios.get('http://localhost:3001/api');
                setWorkout(res.data);
            } catch (err) {
                console.log(err);
            }
        };
        fetchAllWorkout();
    }, []);

    console.log(workouts);
    return ( 
        <div className="row">
             <div className="col-sm-6">
             <div className="card" style={{ border: "1px solid black" }}>
            <div className="card-header">
                <h3>Employee Details</h3>
                    <a href="./add" className="btn btn-primary btn-sm float-end">Add Employee</a>
            </div>
            <div className="card-body">
                <table className="table table-bordered" style={{ border: "0.05px solid black" }}>
                    <thead> 
                        <tr>
                            <th>ID</th>
                            <th>Workouts</th>
                            <th>Load</th>
                            <th>Reps</th>
                            <th>Edit</th>
                            <th>Delete</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            <td id="id" name="id"></td>
                            <td></td>
                            <td></td>
                            <td></td>
                            <td>
                            <form action="./edit" method="POST">
                                <button className="btn btn-success btn-sm" name="edit" value="">Edit</button>
                            </form>
                            </td>
                            <td>
                                <button type="button" className="btn btn-danger btn-sm" data-bs-toggle="modal" data-bs-target="#deleteModal">Delete</button>
                                <div className="modal fade" id="deleteModal" tabIndex="-1" aria-labelledby="deleteModalLabel" aria-hidden="true">
                                    <div className="modal-dialog">
                                        <div className="modal-content">
                                            <div className="modal-body">
                                            Click Confirm to delete
                                            </div>
                                            <div className="modal-footer">
                                            <button type="button" className="btn btn-primary btn-sm" data-bs-dismiss="modal">Close</button>
                                            <form action="/delete" method="POST">
                                                <button className="btn btn-danger btn-sm" name="del" value="<%= row.id %>">Delete</button>
                                            </form>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </td>
                        </tr>
                    </tbody>
                </table>
            </div>
        </div>
             </div>
        </div>
     );
}
 
export default Form;
