import { useEffect, useState } from "react";

const Home = () => {
    const [count, setCount] = useState(0);
    function decrement() {
        setCount(count - 1);
    }

    function increment() {
        setCount(count + 1);
    }
    return ( 
        <div>
            <h2>Home Page!</h2>
            <p>The Count is {count}</p>
            <button onClick={decrement}>-</button>
            <button onClick={increment}>+</button>
        </div>
     );
}
 
export default Home;
