import Axios from "../axios.js";
import { useState } from "react";

const WorkoutForm = () => {
  const [title, setTitle] = useState("");
  const [load, setLoad] = useState("");
  const [reps, setReps] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (title !== "") {
      try {
        const response = await Axios.post("/add", {
          title: title,
          load: load,
          reps: reps,
        });
        window.location.reload();
        console.log(response);
      } catch (error) {
        console.log(error);
      }
    }
  };

  return (
    <div className="card border-solid border-4 rounded-md">
      <form action="">
        <span>
          <h2 className="text-2xl font-bold">Login</h2>
        </span>
        <label className="font-semibold">Enter Username :</label>
        <input
          required
          type="text"
          value={username}
          placeholder="username"
          onChange={(e) => {
            setUsername(e.target.value);
          }}
        ></input>
        <label className="font-semibold">Enter Password :</label>
        <input
          required
          type="password"
          value={password}
          placeholder="password"
          onChange={(e) => {
            setPassword(e.target.value);
          }}
        ></input>
        <button
          className="font-bold text-xl bg-gradient-to-r from-green-400 to-blue-500 hover:from-pink-500 hover:to-yellow-500 rounded-md"
          type="submit"
          onClick={handleLogin}
        >
          Login
        </button>
        {error && <p className="errorMsg">{error}</p>}
        <div className="form-footer">
          <hr></hr>
          <p>
            Don't have an account? <Link to="/register">Register</Link>
          </p>
        </div>
      </form>
    </div>
  );
};

export default WorkoutForm;
